import React, { useEffect, useState, useContext } from "react";
import {
  Layout,
  Row,
  Col,
  Image,
  Card,
  Skeleton,
  Upload,
  message,
  Button,
} from "antd";
import "./profil.css";
import { ProfileForm } from "../Form/ProfileForm";
import { UploadOutlined } from "@ant-design/icons";
import axios from "axios";
import AuthContext from "../../../context/auth-context";

const getIdDoctor = () => {
  const storedId = sessionStorage.getItem("id");
  return storedId ? storedId : "";
};

export const Profil = () => {
  const [doctorBio, setDoctorBio] = useState("");
  const [photo, setPhoto] = useState("");
  const doctorId = getIdDoctor();

  const authCtx = useContext(AuthContext);
  const token = authCtx.token;

  useEffect(() => {
    axios({
      method: "post",
      url: `http://localhost:8000/doctor/user-profile`,
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((response) => {
        return response;
      })
      .then((result) => {
        setPhoto(result.data.message);
      })
      .catch(() => {});

    axios({
      method: "get",
      url: `http://localhost:8000/doctor/bio?id=${doctorId}`,
    })
      .then((response) => {
        return response;
      })
      .then((result) => {
        setDoctorBio(result.data.user);
      });
  }, [token, doctorId]);

  // * Send Photo base 64
  const sendPhoto = (base64) => {
    const photo = {
      photo: base64,
    };

    axios({
      method: "post",
      url: `http://localhost:8000/doctor/add-profile-photo`,
      headers: { Authorization: `Bearer ${token}` },
      data: photo,
    })
      .then((response) => {
        return response;
      })
      .then((result) => {
        // update state photo
        setPhoto(base64);
        message.success(result.data.message);
      })
      .catch(() => {
        message.error("file upload failed.");
      });
  };

  const props = {
    name: "file",
    action: "http://localhost:8000/doctor/add-profile-photo",
    headers: {
      authorization: `Bearer ${token}`,
    },
    onChange(info) {
      if (info.file.status === "done") {
        let reader = new FileReader();
        reader.onloadend = () => {
          sendPhoto(reader.result);
        };
        reader.readAsDataURL(info.file.originFileObj);
      } else if (info.file.status === "error") {
        message.error(`${info.file.name} file upload failed.`);
      }
    },
  };

  return (
    <Layout>
      <Row
        gutter={16}
        justify="center"
        className="profil-detail-stat"
        style={{ height: "100%" }}
        data-aos="fade-up"
      >
        <Col span={20}>
          <Card className="profil-card">
            {photo ? (
              <div
                style={{
                  display: "flex",
                  flexDirection: "column",
                  alignItem: "center",
                  textAlign: "center",
                }}
              >
                <Image
                  width={280}
                  height={280}
                  style={{ marginBottom: "30px" }}
                  src={`${photo}`}
                  fallback="data:image/png;base64,"
                />
                <Upload {...props}>
                  <Button icon={<UploadOutlined />}>Unggah Foto</Button>
                </Upload>
              </div>
            ) : (
              <Skeleton active style={{ width: "20px" }} />
            )}

            {photo ? <ProfileForm biodata={doctorBio} /> : <Skeleton active />}
          </Card>
        </Col>
      </Row>
    </Layout>
  );
};
