import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { Layout, Row, Col, Card, Tabs, Image, Input, Skeleton } from "antd";
import "./rekamMedisDetail.css";
import { Sensors } from "../Chart/Sensors";
import { Penanganan } from "./Penanganan/Penanganan";
import { KontakPasienMap } from "../Maps/KontakPasienMap";
import axios from "axios";

const { TabPane } = Tabs;
const { TextArea } = Input;

export const RekamMedisDetail = () => {
  let { id } = useParams();
  const [biodata, setBiodata] = useState();

  useEffect(() => {
    axios({
      method: "get",
      url: `http://localhost:8000/doctor/patient?id=${id}`,
    })
      .then((response) => {
        return response;
      })
      .then((result) => {
        setBiodata(result.data.result[0]);
      })
      .catch(() => {});
  }, [id]);

  function callback(key) {
    console.log(key);
  }

  return (
    <Layout>
      <Row
        gutter={16}
        justify="center"
        className="record-list"
        style={{ height: "100%" }}
        data-aos="fade-up"
      >
        <Col span={7}>
          <Card style={{ width: 300 }}>
            {biodata ? (
              <div>
                <Image
                  style={{ marginBottom: "10px" }}
                  width={250}
                  src={`${biodata.photo}`}
                  fallback="data:image/png;base64,"
                />
                <Input
                  style={{ marginBottom: "10px" }}
                  placeholder="Nama"
                  value={biodata ? biodata.name : ""}
                />
                <Input
                  style={{ marginBottom: "10px" }}
                  placeholder="Umur"
                  value={biodata ? biodata.tanggal_lahir : ""}
                />
                <Input
                  style={{ marginBottom: "10px" }}
                  placeholder="Spo2"
                  value={biodata ? biodata.spo2 : ""}
                />
                <Input
                  style={{ marginBottom: "10px" }}
                  placeholder="Bpm"
                  value={biodata ? biodata.bpm : ""}
                />
                <Input
                  style={{ marginBottom: "10px" }}
                  placeholder="phone"
                  value={biodata ? biodata.phone : ""}
                />
                <Input
                  style={{ marginBottom: "10px" }}
                  placeholder="status"
                  value={biodata ? biodata.status : ""}
                />
                <TextArea
                  style={{ marginBottom: "10px" }}
                  placeholder="Alamat"
                  value={biodata ? biodata.alamat : ""}
                />
              </div>
            ) : (
              <Skeleton />
            )}
          </Card>
        </Col>
        <Col span={15}>
          <Card style={{ width: "100%" }}>
            {biodata ? (
              <Tabs
                defaultActiveKey="1"
                onChange={callback}
                style={{ width: "100%" }}
              >
                <TabPane tab="Hasil Monitoring" key="1">
                  <Sensors patientId={id} />
                </TabPane>
                <TabPane tab="Kontak Erat" key="2">
                  <KontakPasienMap patientId={id} />
                </TabPane>
                <TabPane tab="Penanganan" key="3">
                  <Penanganan pasien={biodata} />
                </TabPane>
              </Tabs>
            ) : (
              <Skeleton />
            )}
          </Card>
        </Col>
      </Row>
    </Layout>
  );
};
